import React, { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../context/AuthContext';
import './Navbar.css';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isProfileMenuOpen, setIsProfileMenuOpen] = useState(false);
  const { user, logout } = useContext(AuthContext);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleProfileMenu = () => {
    setIsProfileMenuOpen(!isProfileMenuOpen);
  };

  const handleLogout = () => {
    logout();
    setIsProfileMenuOpen(false);
  };

  return (
    <nav className="navbar">
      <div className="nav-container">
        <Link to="/" className="nav-logo">
          <i className="fas fa-leaf"></i> FLORIST
        </Link>
        
        <div className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
          <Link to="/" className="nav-link" onClick={() => setIsMenuOpen(false)}>
            Home
          </Link>
          <Link to="/products" className="nav-link" onClick={() => setIsMenuOpen(false)}>
            Products
          </Link>
          <Link to="/cart" className="nav-link" onClick={() => setIsMenuOpen(false)}>
            Cart
          </Link>
          {!user ? (
            <Link to="/login" className="nav-link" onClick={() => setIsMenuOpen(false)}>
              Login
            </Link>
          ) : (
            <div className="profile-menu-container">
              <div className="profile-icon" onClick={toggleProfileMenu}>
                <i className="fas fa-user-circle"></i>
                <span>{user.name || user.email}</span>
                <i className={`fas fa-chevron-${isProfileMenuOpen ? 'up' : 'down'}`}></i>
              </div>
              {isProfileMenuOpen && (
                <div className="profile-dropdown">
                  <div className="profile-info">
                    <i className="fas fa-user"></i>
                    <span>{user.name || user.email}</span>
                  </div>
                  <hr />
                  <Link to="/profile" className="dropdown-item" onClick={() => setIsProfileMenuOpen(false)}>
                    <i className="fas fa-user"></i> Profil Saya
                  </Link>
                  <Link to="/orders" className="dropdown-item" onClick={() => setIsProfileMenuOpen(false)}>
                    <i className="fas fa-shopping-bag"></i> Pesanan Saya
                  </Link>
                  <hr />
                  <button className="dropdown-item logout-btn" onClick={handleLogout}>
                    <i className="fas fa-sign-out-alt"></i> Logout
                  </button>
                </div>
              )}
            </div>
          )}
        </div>
        
        <div className="nav-toggle" onClick={toggleMenu}>
          <span className="bar"></span>
          <span className="bar"></span>
          <span className="bar"></span>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
