import React, { useState, useEffect, useRef } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Navbar.css';

const Navbar = () => {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const { currentUser, logout } = useAuth();
  const navigate = useNavigate();
  const dropdownRef = useRef(null);

  // Fungsi untuk menutup dropdown saat klik di luar area
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setIsDropdownOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const handleLogout = async () => {
    try {
      await logout();
      navigate('/login');
      closeAllMenus();
    } catch (error) {
      console.error("Failed to log out:", error);
    }
  };

  const closeAllMenus = () => {
    setIsMenuOpen(false);
    setIsDropdownOpen(false);
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleDropdown = () => {
    setIsDropdownOpen(!isDropdownOpen);
  };

  return (
    <nav className="navbar">
      <div className="nav-container">
        <Link to="/" className="nav-logo" onClick={closeAllMenus}>
          <i className="fas fa-leaf"></i> FLORIST
        </Link>

        {/* --- Mobile Menu Icon --- */}
        <div className="nav-toggle" onClick={toggleMenu}>
          <span className="bar"></span>
          <span className="bar"></span>
          <span className="bar"></span>
        </div>

        <div className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
          {/* --- LOGIKA KONDISIONAL UNTUK MENU NAVIGASI --- */}
          {currentUser && currentUser.role === 'admin' ? (
            // 1. Tampilan KHUSUS untuk ADMIN
            <>
              <Link to="/admin" className="nav-link" onClick={closeAllMenus}>
                Admin Panel
              </Link>
            </>
          ) : (
            // 2. Tampilan untuk BUYER dan PENGUNJUNG
            <>
              <Link to="/" className="nav-link" onClick={closeAllMenus}>
                Home
              </Link>
              <Link to="/products" className="nav-link" onClick={closeAllMenus}>
                Products
              </Link>
            </>
          )}

          {/* --- LOGIKA KONDISIONAL UNTUK AUTENTIKASI --- */}
          {currentUser ? (
            // 3. Jika user LOGIN (Admin atau Buyer)
            <div className="profile-menu-container" ref={dropdownRef}>
              <div className="profile-icon" onClick={toggleDropdown}>
                <i className="fas fa-user-circle"></i>
                <span>{currentUser.nama_user || 'User'}</span>
                <i className={`fas fa-chevron-down ${isDropdownOpen ? 'fa-rotate-180' : ''}`}></i>
              </div>

              {isDropdownOpen && (
                <div className="profile-dropdown">
                  <div className="profile-info">
                    <i className="fas fa-user"></i>
                    {currentUser.nama_user}
                  </div>
                  <div className="dropdown-divider"></div>
                  <Link to="/profile" className="dropdown-item" onClick={closeAllMenus}>
                    <i className="fas fa-user-edit"></i> Profile
                  </Link>
                  <Link to="/my-orders" className="dropdown-item" onClick={closeAllMenus}>
                    <i className="fas fa-box"></i> Pesanan Saya
                  </Link>
                  <div className="dropdown-divider"></div>
                  <button onClick={handleLogout} className="dropdown-item logout-btn">
                    <i className="fas fa-sign-out-alt"></i> Logout
                  </button>
                </div>
              )}
            </div>
          ) : (
            // 4. Jika user BELUM LOGIN
            <Link to="/login" className="nav-link" onClick={closeAllMenus}>
              Login
            </Link>
          )}
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
